import React, { useState } from "react";
import { useNavigate, useSearchParams } from "react-router-dom";
const products = [
  {
    id: 1,
    name: "Dien thoai Iphone 15 Pro",
    price: 3000000,
    image:
      "data:image/jpeg;base64,",
  },
  {
    id: 2,
    name: "Dien thoai Oppo Reno11 5G",
    price: 6000000,
    image:
      "data:image/jpeg;base64,",
  },
  {
    id: 3,
    name: "Dien thoai Vivo Y17s",
    price: 9000000,
    image:
      "data:image/jpeg;base64,",
  },
  {
    id: 4,
    name: "Dien thoai Nokia 8210 4G",
    price: 10000000,
    image:
      "data:image/jpeg;base64,",
  },
];
localStorage.setItem("products", JSON.stringify(products));
export default function ListProduct() {
    const [search, setSearch]=useState("");
    const [searchParams, setSearchParams]=useSearchParams();
    const navigate = useNavigate();
    const target= searchParams.get("name") || "";
  return <div>
        <input type="text" value={search} onChange={(e: React.ChangeEvent<HTMLInputElement>)=>setSearch(e.target.value)}/>
        <button onClick={()=>setSearchParams({name: search})}>Tim kiem</button>
        <br />
        <div style={{display: "flex", gap: "15px", marginTop: "10px"}}>
            {products.map((item, index) => {
                if(item.name.toLowerCase().includes(target.toLowerCase())){
                    return <div key={index} style={{display: "flex", flexDirection: "column", gap: "10px", width: "100px", height: "150px"}}>
                        <img src={item.image} alt="" />
                        <div>{item.name}</div>
                        <div>{item.price.toLocaleString()} VND</div>
                        <button onClick={()=>navigate(`/${index}`)}>Xem chi tiet</button>
                    </div>
                }
            })}
        </div>
    </div>
}
